	resend_cfm_msg_loading = function() {
		var c_start = 59;
		$('#btn_c_resend').addClass('disable').attr('disabled', 'disabled');
		$('#btn_c_resend').text(c_start + '秒后重发');
		if (_interval_msg) {} else {
			_interval_msg = setInterval(function() {
				c_start--;
				$('#btn_c_resend').text(c_start + '秒后重发');
				console.log(c_start + '秒后重发');
				if (c_start <= 1) {
					$('#btn_c_resend').text('重发验证短信');
					clearInterval(_interval_msg);
					_interval_msg = undefined;
					$('#btn_c_resend').removeClass('disable').removeAttr('disabled');
					console.log('*** the confirm loading is end!');
				}
			}, 1000);
		}
	};

if (!String.prototype.trim) {
	String.prototype.trim = function() {
		return this.replace(/^s+|s+$/g, '');
	}
}

function trim(str) {
	return str.replace(/(^\s*)|(\s*$)/g, "");
}

function getQueryString(name) {
	var reg = new RegExp("(^|&)" + name + "=([^&]*)(&|$)", "i");
	var r = window.location.search.substr(1).match(reg);
	if (r != null) return decodeURI(r[2]);
	return null;
}